SELECT piz.name AS pizza_name, o.price, piz2.name AS pizzeria_name, v.visit_date FROM person p JOIN person_order o ON p.id = o.person_id JOIN pizza piz ON o.pizza_id = piz.id JOIN person_visits v ON p.id = v.person_id AND v.visit_date = o.visit_date JOIN pizzeria piz2 ON o.pizzeria_id = piz2.id WHERE p.name = 'Kate' AND o.price BETWEEN 800 AND 1000 ORDER BY piz.name, o.price, piz2.name;
